import React from "react";
import '../style/tour-details.css';
import { Container, Row, Col, Form, ListGroup } from 'reactstrap';
import { useParams } from 'react-router-dom';
import tourData from '../assets/data/tours';

const TourDetails = () => {

    const { id } = useParams()
    // this is static data later we will aall out api and load our data from database
    const tour = tourData.find(tour => tour.id === id)
    //destucture properties from tour object 
    const { photo, title, desc, price, review, city, distance, maxGroupSize } = tour

    return <>
        <section>
            <Container>
                <Row>
                    <Col lg='8'>
                        <div className="tour__content">
                            <img src={photo} alt="" />

                            <div className="tour__info">
                                <h2>{title}</h2>

                                <div className="d-flex align-items-center gap-5">

                                    <span className="tour__rating d-flex align-item-center gap-1">
                                        <i class="ri-star-fill"></i>{avgRating === 0 ? null : avgRating}
                                        {totalRating === 0 ? (
                                            "Not rated"
                                        ) : (
                                            <span>({review.length})</span>
                                        )}
                                    </span>
                                </div>

                            </div>
                        </div>
                    </Col>
                </Row>
            </Container>
        </section>
    </>
};

export default TourDetails;
