import React from "react";
import "../style/home.css";
import { Container, Row, Col } from "reactstrap";
import heroImg from "../assets/images/hero-img01.jpg";
import heroImg02 from "../assets/images/hero-img02.jpg";
import heroVideo from "../assets/images/hero-video.mp4";
import worldImg from "../assets/images/world.png";
import Subtitle from "../shared/Subtitle.jsx";

const Home = () => {
  return (
    <>
      {/*========================== Hero Section ==========================*/}
      <section>
        <Container>
          <Row className="align-items-center">
            {/* Left Content Section */}
            <Col lg="6">
              <div className="hero__content">
                <div className="hero__subtitle d-flex align-items-center">
                  <Subtitle subtitle={"Know Before You Go"} />
                  <img src={worldImg} alt="World Map" />
                </div>
                <h1>
                  Traveling Opens the Door to Creating
                  <span className="highlight">Memories</span>
                </h1>
                <p>
                  Lorem ipsum dolor sit amet, consectetur adipisicing elit.
                  Fugit, doloribus. Dolore molestiae vitae pariatur dolorem odit
                  ipsum blanditiis ullam, qui nam voluptate sunt temporibus amet
                  nostrum, corporis, adipisci vero fugiat.
                </p>
              </div>
            </Col>
           {/* Right Content Section - Images & Video */}
           <Col lg="6">
              <div className="hero__media">
                <img src={heroImg} alt="Traveler" className="img-fluid hero-img" />
                <img src={heroImg02} alt="Nature" className="img-fluid hero-img" />
                <video src={heroVideo} controls className="hero-video"></video>
              </div>
            </Col>
          </Row>
        </Container>
      </section>
    </>
  );
};

export default Home;
