import React, { useState, useContext } from 'react';
import './booking.css';
import {
  Form,
  FormGroup,
  ListGroup,
  ListGroupItem,
  Button
} from 'reactstrap';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../../context/authcontext';
import { BASE_URL } from '../../utils/config';

const Booking = ({ tour, avgRating }) => {
  const { price, reviews, title } = tour;
  const navigate = useNavigate();
  const { user } = useContext(AuthContext);

  const [bookingData, setBookingData] = useState({
    userId: user?._id || '',
    userEmail: user?.email || '',
    tourName: title || '',
    fullName: '',
    phone: '',
    guestSize: 1,
    bookAt: ''
  });

  const handleInputChange = (e) => {
    const { id, value } = e.target;
    setBookingData(prev => ({
      ...prev,
      [id]: value
    }));
  };

  const serviceFee = 10;
  const totalAmount = Number(price) * Number(bookingData.guestSize) + serviceFee;

  const handleBookingSubmit = async (e) => {
    e.preventDefault();

    if (!user) {
      return alert('Please sign in to book a tour.');
    }

    try {
      const response = await fetch(`${BASE_URL}/booking`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        credentials: 'include',
        body: JSON.stringify(bookingData),
      });

      const result = await response.json();

      if (!response.ok) {
        return alert(result.message);
      }

      navigate('/thank-you');
    } catch (error) {
      alert('Booking failed: ' + error.message);
    }
  };

  return (
    <div className='booking'>

      {/* Top Info */}
      <div className='booking__top d-flex align-items-center justify-content-between'>
        <h3>${price}<span>/per person</span></h3>
        <span className="tour__rating d-flex align-items-center">
          <i className="ri-star-fill"></i>
          {avgRating > 0 && `${avgRating} (${reviews?.length})`}
        </span>
      </div>

      {/* Booking Form */}
      <div className="booking__form">
        <h5>Information</h5>
        <Form className='booking__info-form' onSubmit={handleBookingSubmit}>
          <FormGroup>
            <input
              type="text"
              id="fullName"
              placeholder="Full Name"
              required
              onChange={handleInputChange}
            />
          </FormGroup>
          <FormGroup>
            <input
              type="number"
              id="phone"
              placeholder="Phone"
              required
              onChange={handleInputChange}
            />
          </FormGroup>
          <FormGroup className="d-flex align-items-center gap-3">
            <input
              type="date"
              id="bookAt"
              required
              onChange={handleInputChange}
            />
            <input
              type="number"
              id="guestSize"
              placeholder="Guests"
              min="1"
              required
              onChange={handleInputChange}
            />
          </FormGroup>
          <Button type="submit" className="btn primary__btn w-100 mt-4">
            Book Now
          </Button>
        </Form>
      </div>

      {/* Booking Summary */}
      <div className="booking__bottom">
        <ListGroup>
          <ListGroupItem className="border-0 px-0">
            <h5 className='d-flex align-items-center gap-1'>
              ${price} <i className="ri-close-line"></i> {bookingData.guestSize} Person
            </h5>
            <span>${price * bookingData.guestSize}</span>
          </ListGroupItem>
          <ListGroupItem className="border-0 px-0">
            <h5>Service Charge</h5>
            <span>${serviceFee}</span>
          </ListGroupItem>
          <ListGroupItem className="border-0 px-0 total">
            <h5>Total</h5>
            <span>${totalAmount}</span>
          </ListGroupItem>
        </ListGroup>
      </div>

    </div>
  );
};

export default Booking;
