import React, { useRef, useState } from "react";
import '../style/tour-details.css';
import { Container, Row, Col, ListGroup } from 'reactstrap';
import { useParams } from 'react-router-dom';
import tourData from '../assets/data/tours';
import calculateAvgRating from "../utils/avgRating";
import avatar from "../assets/images/avatar.jpg";
import Booking from "../components/Booking/Booking";
// import { useState } from 'react';

const TourDetails = () => {

    const { id } = useParams()

    const reviewMsgRef = useRef(' ')
    const [tourRating, setTourRating] = useState(null)
    // This is static data, later we will call out an API and load our data from the database
    const tour = tourData.find(tour => tour.id === id);
    // Destructure properties from the tour object 
    const { photo, title, desc, price, reviews, address, city, distance, maxGroupSize } = tour;

    const { totalRating, avgRating } = calculateAvgRating(reviews);

    // Format date
    const options = { day: 'numeric', month: "long", year: "numeric" };

    // Submit request to the server
    const submitHandler = (e) => {
        e.preventDefault();
        const reviewText = reviewMsgRef.current.value;
        alert(`${reviewText}, ${tourRating}`);
        // Later we will call an API or perform other actions
    }

    return <>
        <section>
            <Container>
                <Row>
                    <Col lg='8'>
                        <div className="tour__content">
                            <img src={photo} alt="" />

                            <div className="tour__info">
                                <h2>{title}</h2>

                                <div className="d-flex align-items-center gap-5">

                                    <span className="tour__rating d-flex align-items-center gap-1">
                                        <i className="ri-star-fill" style={{ 'color': "var(--secondary-color)" }}></i>
                                        {avgRating === 0 ? null : avgRating}
                                        {totalRating === 0 ? (
                                            "Not rated"
                                        ) : (
                                            <span>({reviews?.length})</span>
                                        )}
                                    </span>

                                    <span>
                                        <i className="ri-map-pin-fill"></i> {address}
                                    </span>
                                </div>

                                <div className="tour__extra-details">
                                    <span><i className='ri-map-pin-2-line'></i>{city}</span>
                                    <span><i className='ri-money-dollar-circle-line'></i> ${price} /per person</span>
                                    <span><i className='ri-map-pin-time-line'></i> {distance} k/m</span>
                                    <span><i className='ri-group-line'></i>{maxGroupSize} Peoples</span>
                                </div>
                                <h5>Description</h5>
                                <p>{desc}</p>

                            </div>

                            {/**=======================tour reviews section=============================== */}
                            <div className="tour__review mt-5">
                                <h4>Review({reviews?.length} reviews)</h4>

                                <form onSubmit={submitHandler}>
                                    <div className="d-flex align-items-center gap-3 mb=4">
                                        <span onClick={() => setTourRating(1)}>1<i className="ri-star-s-fill"></i></span>
                                        <span onClick={() => setTourRating(2)}>2<i className="ri-star-s-fill"></i></span>
                                        <span onClick={() => setTourRating(3)}>3<i className="ri-star-s-fill"></i></span>
                                        <span onClick={() => setTourRating(4)}>4<i className="ri-star-s-fill"></i></span>
                                        <span onClick={() => setTourRating(5)}>5<i className="ri-star-s-fill"></i></span>
                                    </div>

                                    <div className="review__input">
                                        <input type="text" ref={reviewMsgRef} placeholder="share your thoughts" required />
                                        <button className="btn primary__btn text-white">Submit</button>
                                    </div>
                                </form>

                                <ListGroup className="user__reviews">
                                    {
                                        reviews?.map((review, index) => (  
                                            <div className="review__item" key={index}>  {/* Added key={index} */}
                                                <img src={avatar} alt="" />

                                                <div className="w-100">
                                                    <div className="d-flex align-items-center justify-content-between">
                                                        <div>
                                                            <h5>Raj</h5>
                                                            <p>{new Date("01-08-2025").toLocaleDateString("en-Us", options)}</p>
                                                        </div>
                                                        <span className="d-flex align-items-center">
                                                            5<i className="ri-star-s-fill"></i>
                                                        </span>
                                                    </div>
                                                    <h6>Amazing Tour</h6>
                                                </div>

                                            </div>
                                        ))
                                    }
                                </ListGroup>
                            </div>
                            {/**=======================tour reviews section end =============================== */}
                        </div>
                    </Col>
                    <Col lg='4'>
                        <Booking tour={tour} avgRating={avgRating} />
                    </Col>
                </Row>
            </Container>
        </section>
    </>
};

export default TourDetails;
