import React, { useContext, useState } from "react" 
import "./booking.css"
import { Form, FormGroup, ListGroup, ListGroupItem, Button } from "reactstrap"
import { AuthContext } from "../../context/authcontext.js"
import { BASE_URL } from "../../utils/config.js"
import { useNavigate } from "react-router-dom"

const Booking = ({ tour, avgRating }) => {
  const { price, reviews, title } = tour
  const navigate = useNavigate()

  const { user } = useContext(AuthContext)
  const [booking, setBooking] = useState({
    userId: user ? user._id : null,
    userEmail: user ? user.email : "",
    tourName: title,
    fullName: "",
    phone: "",
    guestSize: 1,
    bookAt: "",
  })

  const handleChange = (e) => {
    setBooking({ ...booking, [e.target.id]: e.target.value })
  }

  const handleClick = async (e) => {
    e.preventDefault()

    try {
      if (!user || user === undefined || user === null) {
        return alert("Please sign in")
      }

      const res = await fetch(`${BASE_URL}/booking`, {
        method: "post",
        headers: {
          "content-type": "application/json",
        },
        credentials: "include",
        body: JSON.stringify(booking),
      })

      const result = await res.json()

      if (!res.ok) {
        return alert(result.message)
      }

      navigate("/thank-you")
    } catch (err) {
      alert("Booking failed.")
    }
  }

  const serviceFee = 10
  const totalAmount = Number(price) * Number(booking.guestSize) + serviceFee

  return (
    <div className="booking">
      <div className="booking__top d-flex align-items-center justify-content-between">
        <h3>
          ${price} <span>/per person</span>
        </h3>
        <span className="tour__rating d-flex align-items-center">
          <i className="ri-star-fill"></i>
          {avgRating === 0 ? null : avgRating}
          {reviews?.length === 0 ? (
            "Not rated"
          ) : (
            <span>({reviews?.length})</span>
          )}
        </span>
      </div>

      {/* =========== booking form ============= */}
      <div className="booking__form">
        <h5>Information</h5>
        <Form className="booking__info-form" onSubmit={handleClick}>
          <FormGroup>
            <input
              type="text"
              placeholder="Full Name"
              id="fullName"
              required
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <input
              type="number"
              placeholder="Phone"
              id="phone"
              required
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup className="d-flex align-items-center gap-3">
            <input
              type="date"
              placeholder=""
              id="bookAt"
              required
              onChange={handleChange}
            />
            <input
              type="number"
              placeholder="Guest"
              id="guestSize"
              required
              onChange={handleChange}
            />
          </FormGroup>
          <Button className="btn primary__btn w-100 mt-4" type="submit">
            Book Now
          </Button>
        </Form>
      </div>

      {/* =========== booking bottom ============= */}
      <div className="booking__bottom">
        <ListGroup>
          <ListGroupItem className="border-0 px-0">
            <h5 className="d-flex align-items-center gap-1">
              ${price} <i className="ri-close-line"></i> {booking.guestSize} Person
            </h5>
            <span>${price * booking.guestSize}</span>
          </ListGroupItem>
          <ListGroupItem className="border-0 px-0">
            <h5>Service Charge</h5>
            <span>${serviceFee}</span>
          </ListGroupItem>
          <ListGroupItem className="border-0 px-0 total">
            <h5>Total</h5>
            <span>${totalAmount}</span>
          </ListGroupItem>
        </ListGroup>
      </div>
    </div>
  )
}

export default Booking
