import React ,{useState,useEffect} from "react";
import CommonSection from "../shared/CommonSection";
import './../style/tour.css';
import tourData from '../assets/data/tours';
import TourCard from './../shared/TourCard';
import SearchBar from './../shared/SearchBar';
import Newletter from './../shared/Newletter';
import { Container , Row ,Col  } from 'reactstrap';

import useFetch from '../components/hooks/useFetch'
import { BASE_URL } from '../utils/config'
import tours from "../assets/data/tours";

const Tours =()=>{

  const [ pageCount , setPageCount ] = useState(0)
  const [ page,setPage ] = useState(0)

  const {data:tours, loading, error} = useFetch(`${BASE_URL}/tours?page=${page}`)
  const {data:tourCount} = useFetch(`${BASE_URL}/tours/search/getTourCount`)

  useEffect(() => {
    const pages = Math.ceil(tourCount / 8);
      setPageCount(pages);
      // window.scrollTo(0,0)
    }, [page, tourCount]);


  // useEffect(() =>{
  //   const pages = Math.ceil(5/4) //later will be used in backend data counting 
  //   setPageCount(pages);

    return <>
      <CommonSection title={'ALL TOURS'} />
      <section >
        <Container>

            {loading && <h4 className='text-center pt-5'>Loading......</h4> }
            {error && <h4 className='text-center pt-5'>{error}</h4> } 

            <Row>
                <SearchBar />
            </Row>
        </Container>
      </section>
      <section className="pt-0"> 
        <Container>
          {loading && <h4 className="text-center pt-5">Loading......</h4>}
          {!loading && !error && 
            <Row>
                   { tours ?.map(tour => (<Col lg='3' className="mb-4" key={tour.id}><TourCard tour={tour}/> </Col>))}
                   <Col lg='12'>
                     <div className="pagination d-flex align-items-center justify-content-center mt-4 gap-3">
                      {[...Array(pageCount).keys()].map(number =>(
                        <span key={number} onClick={()=> setPage(number)} className={page===number ? 'active__page' : " " }>
                          {number +1}
                        </span>
                      ))}

                     </div>
                   </Col>
            </Row>
            }
        </Container>
      </section>
      
      <Newletter />
    
    </>
} ;

export default Tours;
